import React, { useEffect, useState } from 'react';
import api from '../api';

const Home = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const res = await api.get('/products');
      setProducts(res.data);
    };
    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Clothes Store</h1>
      <div>
        {products.map(p => (
          <div key={p.id}>
            <h3>{p.name}</h3>
            <p>Category: {p.category}</p>
            <p>Price: ${p.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
